import React, {Component} from "react";
import { BrowserRouter, Route, Link } from "react-router-dom";
import GameRoomTwoPNG from './GameRoomTwoPNG.png';
import DamageEffects from "../DamageEffects/DamageEffects";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';



const RoomTwo = (props) => {

    const showToastMessage = () => {
        toast.success('TEST Success Notification !', {
            position: toast.POSITION.TOP_RIGHT
        });
    }

    if(props.roomTwoStatus === 0){
        return (
            <nav>
                <div>
                    <h2>This Is Room Two</h2>
                </div>
                <div>
                    <DamageEffects playerDamageDone={props.playerDamageDone} enemyDamageDone={props.enemyDamageDone} />
                </div>
                <div>
                    <h2>You are encountering a dangerous circle. It has {props.enemyHealth} health, {props.enemyAttackLow} attackLow, {props.enemyAttackHigh} attackHigh, {props.enemySpeed} speed, {props.enemyArmor} armor, and will reward you with {props.enemyReward} coin. </h2>
                </div>
                <div>
                    {/* <button className="attackMove" onClick={() => props.playerAttackMove(1), showToastMessage}>Attack</button> */}
                    <ToastContainer />
                    <button className="attackMove" onClick={() =>props.playerAttackMove(1)}>Attack enemy 1</button>
                    <button className="dodgeMove" onClick={() => props.playerDodgeMove()}>Dodge Attack</button>
                    <button className="healMove">Heal</button>
                </div>
                <div>
                    <img src={GameRoomTwoPNG} alt="GameRoomTwoPNG" />
                </div>
            </nav>
        )
    }

    if (props.roomTwoStatus === 1 ) {
        return(
            <nav>
                <div>
                    <h2>This Is The GameBoard Room Two</h2>
                </div>
            <div>
                <h2>You killed a dangerous circle. It rewarded you with {props.enemyReward} coin. </h2>
                {/* <button className="circle" onClick={() => makeCircleEnemy(20, 2, 0, 1)}>EncounterCircleEnemy</button> */}
            </div>
            <ul>
                <li>
                    <Link to="/GameBoard" >
                        <button className="gameBoardButton" onClick={() => props.resetRoomStatus()}>Go to room One</button>
                    </Link>
                </li>
                <li>
                    <Link to="/RoomThree" >
                    <button className="square" onClick={() => props.roomMovement(3)}>Go to room Three</button>
                    </Link>
                </li>
                <li>
                    <Link to="/RoomSix">
                        <button className="circle" onClick={() => props.roomMovement(6)}>Go to room Six</button>
                    </Link>
                </li>
            </ul>
            <div>
                <img src={GameRoomTwoPNG} alt="GameRoomTwoPNG" />
            </div>
            </nav>
        )
    }
}

export default RoomTwo;
