import React , { Component } from 'react';
import { useEffect } from 'react';
import { BrowserRouter, Route, Link } from "react-router-dom";
import PlayerAttackButton from '../PlayerAttackButton/PlayerAttackButton'
import PlayerCloseEnemyAttackButton from '../PlayerCloseEnemyAttackButton'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';


const DamageEffects = (props) => {
    if(props.startCombatCheck === false){
        return(
            <div>
                <button className="timedCombatSequenceTest" onClick={() => props.startCombat()}>Start Combat!</button>
            </div>
        )
    }
    if(props.startCombatCheck === true){
        return(
            <div>
                <div>
                    <PlayerAttackButton useHealthPotion={props.useHealthPotion} resetRoomStatus={props.resetRoomStatus} numberOfEnemiesInRoom={props.numberOfEnemiesInRoom} playerAttacked={props.playerAttacked} playerAttackMove={props.playerAttackMove} playerAttackClosestEnemyMove={props.playerAttackClosestEnemyMove} playerAttackTimerState={props.playerAttackTimerState} playerAttackTimerStateMax={props.playerAttackTimerStateMax} />
                    <PlayerCloseEnemyAttackButton />
                </div>
                <div>
                    <h1>COMBAT STARTED</h1>
                </div>
            </div>
        )
    }
}

export default DamageEffects;

