import React, {Component} from 'react';
import axios from 'axios';



class Comments extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            comments: [],
            comment: ''
        }
    }
    componentDidMount(){
        this.getComments()
    }

    getComments = async() => {
        let response = await axios.get('http://127.0.0.1:8000/comments/')
        this.setState({
            comments: response.data
        })
    }

    render() { 
        return (
            <div>

            </div>
        );
    }
}

export default Comments;
